import React from 'react';
import './App.css';
import Projects from './components/customers/projects';


class App extends React.Component {
  constructor() {
    super();
    this.state = {
        projects: [],
        filters: {
            projectType: [],
            interest: [],
            role: []
        }
    };
  }


  componentDidMount(){
    fetch('/api/projects')
        .then(res => res.json())
        .then(projects => this.setState({projects: projects}, ()=>
            console.log('Projects fetched..', projects)
        )
    );
  }

  render(){
    const projects = this.state.projects;
    const listProjects = projects.map(project => <Projects>{ project }</Projects>);

    return (
      <div className="App">
        <header className="App-header">
          <p>
            Edit <code>src/App.js</code> and save to reload.
          </p> 
          <a
            className="App-link" href="https://reactjs.org" target="_blank" rel="noopener noreferrer"
          >
            Learn React
          </a>
        </header>


        <div>{ listProjects }</div>
      </div>
    );
  }

}

export default App;
